mapper.select:
SELECT cl.id, ci.grade, cl.course_name, cl.course_type, cl.course_description, cl.course_cover_url, cl.default_main_teacher_username, cl.course_identifier, cl.valid, cl.created_time, cl.updated_time, ti.name AS default_main_teacher_name, GROUP_CONCAT(DISTINCT ci.class_name) AS classNames, MIN(ss.start_time) AS recent_course_schedule_time -- 添加这行来获取最小的 start_time
WHERE 1=1
GROUP BY cl.id, ci.grade, cl.course_name, cl.course_type, cl.course_description, cl.course_cover_url, cl.default_main_teacher_username, cl.course_identifier, cl.valid, cl.created_time, cl.updated_time, ti.name ORDER BY cl.created_time DESC LIMIT #{pageNumber}, #{pageSize}
